'use strict';

function openInANewWindow(url, date1, date2) {
    const d1 = new Date(date1).getTime();
    const d2 = new Date(date2).getTime();
    let minute =  Math.round((d2 - d1) / 60000);

    if (minute < 15) {
        window.open(url, "Popup", "location,status,scrollbars,resizable,width=800, height=800");
    } else {
        let remain = minute - 15;
        alert('Live Streaming Will be Available in ' + remain + 'minutes');
    }

}